import{e as n}from"./app.c97851f0.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";var s="/my-notes/assets/blob.46794bd1.jpg";const p={},e=n('<h1 id="\u6587\u4EF6\u64CD\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u6587\u4EF6\u64CD\u4F5C" aria-hidden="true">#</a> \u6587\u4EF6\u64CD\u4F5C</h1><h2 id="blob" tabindex="-1"><a class="header-anchor" href="#blob" aria-hidden="true">#</a> Blob</h2><h3 id="\u5B9A\u4E49" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u4E49" aria-hidden="true">#</a> \u5B9A\u4E49</h3><p>Blob\uFF08Binary Large Object\uFF09\u8868\u793A\u4E8C\u8FDB\u5236\u7C7B\u578B\u7684\u5927\u5BF9\u8C61\u3002\u5728\u6570\u636E\u5E93\u7BA1\u7406\u7CFB\u7EDF\u4E2D\uFF0C\u5C06\u4E8C\u8FDB\u5236\u6570\u636E\u5B58\u50A8\u4E3A\u4E00\u4E2A\u5355\u4E00\u4E2A\u4F53\u7684\u96C6\u5408\u3002Blob \u901A\u5E38\u662F\u5F71\u50CF\u3001\u58F0\u97F3\u6216\u591A\u5A92\u4F53\u6587\u4EF6\u3002</p><p>\u5728 JavaScript \u4E2D Blob \u7C7B\u578B\u7684\u5BF9\u8C61\u8868\u793A\u4E0D\u53EF\u53D8\u7684\u7C7B\u4F3C\u6587\u4EF6\u5BF9\u8C61\u7684\u539F\u59CB\u6570\u636E\u3002</p><p>Blob \u7531\u4E00\u4E2A\u53EF\u9009\u7684\u5B57\u7B26\u4E32 type\uFF08\u901A\u5E38\u662F MIME \u7C7B\u578B\uFF09\u548C blobParts \u7EC4\u6210\uFF1A</p><p><img src="'+s+`" alt=""></p><blockquote><p>MIME\uFF08Multipurpose Internet Mail Extensions\uFF09\u591A\u7528\u9014\u4E92\u8054\u7F51\u90AE\u4EF6\u6269\u5C55\u7C7B\u578B\uFF0C\u662F\u8BBE\u5B9A\u67D0\u79CD\u6269\u5C55\u540D\u7684\u6587\u4EF6\u7528\u4E00\u79CD\u5E94\u7528\u7A0B\u5E8F\u6765\u6253\u5F00\u7684\u65B9\u5F0F\u7C7B\u578B\uFF0C\u5F53\u8BE5\u6269\u5C55\u540D\u6587\u4EF6\u88AB\u8BBF\u95EE\u7684\u65F6\u5019\uFF0C\u6D4F\u89C8\u5668\u4F1A\u81EA\u52A8\u4F7F\u7528\u6307\u5B9A\u5E94\u7528\u7A0B\u5E8F\u6765\u6253\u5F00\u3002\u591A\u7528\u4E8E\u6307\u5B9A\u4E00\u4E9B\u5BA2\u6237\u7AEF\u81EA\u5B9A\u4E49\u7684\u6587\u4EF6\u540D\uFF0C\u4EE5\u53CA\u4E00\u4E9B\u5A92\u4F53\u6587\u4EF6\u6253\u5F00\u65B9\u5F0F\u3002</p><p>\u5E38\u89C1\u7684 MIME \u7C7B\u578B\u6709\uFF1A\u8D85\u6587\u672C\u6807\u8BB0\u8BED\u8A00\u6587\u672C .html text/html\u3001PNG \u56FE\u50CF .png image/png\u3001\u666E\u901A\u6587\u672C .txt text/plain \u7B49\u3002</p></blockquote><h3 id="blob-url-object-url" tabindex="-1"><a class="header-anchor" href="#blob-url-object-url" aria-hidden="true">#</a> Blob URL/Object URL</h3><p>Blob URL/Object URL \u662F\u4E00\u79CD\u4F2A\u534F\u8BAE\uFF0C\u5141\u8BB8 Blob \u548C File \u5BF9\u8C61\u7528\u4F5C\u56FE\u50CF\uFF0C\u4E0B\u8F7D\u4E8C\u8FDB\u5236\u6570\u636E\u94FE\u63A5\u7B49\u7684 URL \u6E90\u3002</p><p>\u6D4F\u89C8\u5668\u5185\u90E8\u4E3A\u6BCF\u4E2A\u901A\u8FC7 <code>URL.createObjectURL</code> \u751F\u6210\u7684 URL \u5B58\u50A8\u4E86\u4E00\u4E2A URL \u2192 Blob \u6620\u5C04\u3002\u751F\u6210\u7684 URL \u4EC5\u5728\u5F53\u524D\u6587\u6863\u6253\u5F00\u7684\u72B6\u6001\u4E0B\u624D\u6709\u6548\u3002\u4F46\u5982\u679C\u4F60\u8BBF\u95EE\u7684 Blob URL \u4E0D\u518D\u5B58\u5728\uFF0C\u5219\u4F1A\u4ECE\u6D4F\u89C8\u5668\u4E2D\u6536\u5230 404 \u9519\u8BEF\u3002</p><p>\u6620\u5C04\u5728\u6587\u6863\u5378\u8F7D\u65F6\u81EA\u52A8\u6E05\u9664\uFF0C\u56E0\u6B64 Blob \u5BF9\u8C61\u968F\u540E\u88AB\u91CA\u653E\u3002\u4F46\u662F\uFF0C\u5982\u679C\u5E94\u7528\u7A0B\u5E8F\u5BFF\u547D\u5F88\u957F\uFF0C\u90A3\u4E0D\u4F1A\u5F88\u5FEB\u53D1\u751F\u3002\u56E0\u6B64\uFF0C\u5982\u679C\u6211\u4EEC\u521B\u5EFA\u4E00\u4E2A Blob URL\uFF0C\u5373\u4F7F\u4E0D\u518D\u9700\u8981\u8BE5 Blob\uFF0C\u5B83\u4E5F\u4F1A\u5B58\u5728\u5185\u5B58\u4E2D\u3002</p><p>\u9488\u5BF9\u8FD9\u4E2A\u95EE\u9898\uFF0C\u6211\u4EEC\u53EF\u4EE5\u8C03\u7528 <code>URL.revokeObjectURL(url)</code> \u65B9\u6CD5\uFF0C\u4ECE\u5185\u90E8\u6620\u5C04\u4E2D\u5220\u9664\u5F15\u7528\uFF0C\u4ECE\u800C\u5141\u8BB8\u5220\u9664 Blob\uFF08\u5982\u679C\u6CA1\u6709\u5176\u4ED6\u5F15\u7528\uFF09\uFF0C\u5E76\u91CA\u653E\u5185\u5B58\u3002</p><h3 id="blob-vs-arraybuffer" tabindex="-1"><a class="header-anchor" href="#blob-vs-arraybuffer" aria-hidden="true">#</a> Blob vs ArrayBuffer</h3><p>ArrayBuffer \u5BF9\u8C61\u7528\u4E8E\u8868\u793A\u901A\u7528\u7684\uFF0C\u56FA\u5B9A\u957F\u5EA6\u7684\u539F\u59CB\u4E8C\u8FDB\u5236\u6570\u636E\u7F13\u51B2\u533A\u3002\u4F60\u4E0D\u80FD\u76F4\u63A5\u64CD\u7EB5 ArrayBuffer \u7684\u5185\u5BB9\uFF0C\u800C\u662F\u9700\u8981\u521B\u5EFA\u4E00\u4E2A TypedArray \u5BF9\u8C61\u6216 DataView \u5BF9\u8C61\uFF0C\u8BE5\u5BF9\u8C61\u4EE5\u7279\u5B9A\u683C\u5F0F\u8868\u793A\u7F13\u51B2\u533A\uFF0C\u5E76\u4F7F\u7528\u8BE5\u5BF9\u8C61\u8BFB\u53D6\u548C\u5199\u5165\u7F13\u51B2\u533A\u7684\u5185\u5BB9\u3002</p><p>Blob \u5BF9\u8C61\u4E0E ArrayBuffer \u5BF9\u8C61\u62E5\u6709\u5404\u81EA\u7684\u7279\u70B9\uFF0C\u5B83\u4EEC\u4E4B\u95F4\u7684\u533A\u522B\u5982\u4E0B\uFF1A</p><ul><li>ArrayBuffer \u63D0\u4F9B\u5199\u5165/\u7F16\u8F91\u7684\u80FD\u529B\uFF0CBlob \u5BF9\u8C61\u662F\u4E0D\u53EF\u53D8\u7684\uFF0C\u800C ArrayBuffer \u662F\u53EF\u4EE5\u901A\u8FC7 TypedArrays \u6216 DataView \u6765\u64CD\u4F5C\u3002</li><li>ArrayBuffer \u662F\u5B58\u5728\u5185\u5B58\u4E2D\u7684\uFF0C\u53EF\u4EE5\u76F4\u63A5\u64CD\u4F5C\u3002\u800C Blob \u53EF\u4EE5\u4F4D\u4E8E\u78C1\u76D8\u3001\u9AD8\u901F\u7F13\u5B58\u5185\u5B58\u548C\u5176\u4ED6\u4E0D\u53EF\u7528\u7684\u4F4D\u7F6E\u3002</li><li>\u867D\u7136 Blob \u53EF\u4EE5\u76F4\u63A5\u4F5C\u4E3A\u53C2\u6570\u4F20\u9012\u7ED9\u5176\u4ED6\u51FD\u6570\uFF0C\u6BD4\u5982 window.URL.createObjectURL()\u3002\u4F46\u662F\uFF0C\u4F60\u53EF\u80FD\u4ECD\u9700\u8981 FileReader \u4E4B\u7C7B\u7684 File API \u624D\u80FD\u4E0E Blob \u4E00\u8D77\u4F7F\u7528\u3002</li><li>Blob \u4E0E ArrayBuffer \u5BF9\u8C61\u4E4B\u95F4\u662F\u53EF\u4EE5\u76F8\u4E92\u8F6C\u5316\u7684\uFF1A <ul><li>\u4F7F\u7528 FileReader \u7684 <code>readAsArrayBuffer()</code> \u65B9\u6CD5\uFF0C\u53EF\u4EE5\u628A Blob \u5BF9\u8C61\u8F6C\u6362\u4E3A ArrayBuffer \u5BF9\u8C61\uFF1B</li><li>\u4F7F\u7528 Blob \u6784\u9020\u51FD\u6570\uFF0C\u5982 <code>new Blob([new Uint8Array(data])</code>;\uFF0C\u53EF\u4EE5\u628A ArrayBuffer \u5BF9\u8C61\u8F6C\u6362\u4E3A Blob \u5BF9\u8C61\u3002</li></ul></li></ul><p>\u5728 AJAX \u573A\u666F\u4E0B\u53EF\u4EE5\u901A\u8FC7 xhr.responseType \u8BBE\u7F6E\u4E0D\u540C\u7684\u6570\u636E\u7C7B\u578B</p><div class="language-javascript ext-js line-numbers-mode"><pre class="language-javascript"><code><span class="token keyword">function</span> <span class="token constant">GET</span><span class="token punctuation">(</span><span class="token parameter">url<span class="token punctuation">,</span> callback</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
  <span class="token keyword">let</span> xhr <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">XMLHttpRequest</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
  xhr<span class="token punctuation">.</span><span class="token function">open</span><span class="token punctuation">(</span><span class="token string">&quot;GET&quot;</span><span class="token punctuation">,</span> url<span class="token punctuation">,</span> <span class="token boolean">true</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
  xhr<span class="token punctuation">.</span>responseType <span class="token operator">=</span> <span class="token string">&quot;arraybuffer&quot;</span><span class="token punctuation">;</span> <span class="token comment">// or xhr.responseType = &quot;blob&quot;;</span>
  xhr<span class="token punctuation">.</span><span class="token function">send</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

  xhr<span class="token punctuation">.</span><span class="token function-variable function">onload</span> <span class="token operator">=</span> <span class="token keyword">function</span> <span class="token punctuation">(</span><span class="token parameter">e</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
    <span class="token keyword">if</span> <span class="token punctuation">(</span>xhr<span class="token punctuation">.</span>status <span class="token operator">!=</span> <span class="token number">200</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
      <span class="token function">alert</span><span class="token punctuation">(</span><span class="token string">&quot;Unexpected status code &quot;</span> <span class="token operator">+</span> xhr<span class="token punctuation">.</span>status <span class="token operator">+</span> <span class="token string">&quot; for &quot;</span> <span class="token operator">+</span> url<span class="token punctuation">)</span><span class="token punctuation">;</span>
      <span class="token keyword">return</span> <span class="token boolean">false</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>
    <span class="token function">callback</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token class-name">Uint8Array</span><span class="token punctuation">(</span>xhr<span class="token punctuation">.</span>response<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// or new Blob([xhr.response]);</span>
  <span class="token punctuation">}</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br></div></div>`,19);function t(o,l){return e}var u=a(p,[["render",t]]);export{u as default};
